INSERT INTO `doctor_doctor` VALUES (2,'1','李枚','副主任医师','哈尔滨医科大学附属第四医院','内科','老年人高血压,老年便秘,老年人心力衰竭。','周二','从2015到至今哈尔滨医科大学附属第四医院',2,2,'1@qq.com'),(3,'2','董玉梅','副主任医师','哈尔滨医科大学附属第四医院','内科','内科疾病','周四、周六','无',2,2,'2@qq.com'),(4,'3','王锡海','主任医师','哈尔滨医科大学附属第二医院','外科','普外科常见病、多发病以及疑难病症的诊断和外科治疗有较丰富的临床经验。','周四、周五','。。。',3,3,'3@qq.com'),(5,'4','吴波','主任医师','哈尔滨医科大学附属第四医院','外科','擅长肝、胆、胃肠、乳腺和甲状腺疾病手术及胆囊腹腔镜微创治疗。','周日','。。。',3,2,'4@qq.com'),(6,'5','刘昶','主任医师','哈尔滨医科大学附属第一医院','内科','腹腔镜胆道、胃、结直肠外科；疝和腹壁外科。','周三','。。。',2,4,'5@qq.com'),(7,'6','刘春富','副主任医师','哈尔滨医科大学附属第四医院','外科','.','周一','无',3,2,'6@qq.com'),(8,'7','韩世愈','副主任医师','哈尔滨医科大学附属第四医院','妇产科','滋养细胞肿瘤、宫颈癌、内子宫膜癌、卵巢癌的手术，微创腔镜及微创介入等方面有独到之处。','周一','无',5,2,'7@qq.com'),(9,'8','史文华','主任医师','哈尔滨医科大学附属第四医院','妇产科','妇产科疾病','周三、周四、周五','收录中',5,2,'8@qq.com'),(10,'9','尹明英','主任医师','哈尔滨医科大学附属第四医院','儿科','。','周五','收录中',6,2,'9@qq.com'),(11,'10','刘雪峰','副主任医师','哈尔滨医科大学附属第四医院','骨科','擅长关节疾病及脊柱微创手术。','周三','收录中',7,2,'10@qq.com'),(12,'11','岳伟杰','主任医师','哈尔滨医科大学附属第四医院','骨科','脊柱、关节、创伤的常规手术治疗和微创手术治疗','周一','收录中',7,2,'11@qq.com'),(13,'12','毕良佳','主治医生','哈尔滨医科大学附属第四医院','口腔科','以口为单位解决口腔全部疾病。特别对牙周病治疗有独到之处','周一','收录中',8,2,'12@qq.com'),(14,'13','韩清','副主任医师','哈尔滨医科大学附属第四医院','眼科','白内障超乳手术，视网膜玻璃体手术，激光治疗眼病。','周一','收录中',9,2,'13@qq.com'),(15,'14','邵青','副主任医师','哈尔滨医科大学附属第四医院','中医科','恶性肿瘤,冠心病,脑中风、脑供血不足,慢性肾脏疾病,消化系统疾病,慢性咽炎,久治不愈咳嗽,急慢性湿疹,痤疮等疑难杂症。','周三、周四、周五','从2012到至今哈尔滨医科大学附属第四医院,从1989到至今黑龙江省中医科学研究院;1984－1989 哈尔滨医科大学 医疗系 学士1993－1995 北京中医药大学 中医系 学士2005－2007 哈尔滨医科大学 中西医 硕士2009－2012 黑龙江中医药大学 中西医 博士2011－2012 美国得克萨斯大学 艾尔帕索分校 肿瘤分子生物学 访问学者',10,2,'14@qq.com'),(16,'15','尤艳','主任医师','哈尔滨医科大学附属第四医院','皮肤病科','湿疹,白癜风,牛皮癣,荨麻疹,痤疮,毛囊炎,皮炎,皮肤过敏皮肤肿瘤','周三、周四','从2015到至今哈医大四院',11,2,'15@qq.com'),(17,'16','徐平','副主任医师','哈尔滨医科大学附属第四医院','耳鼻喉科','中耳炎、鼻窦炎、鼾症、眩晕、喉肿瘤。','周四、周五','收录中..',12,2,'16@qq.com'),(18,'17','任仲侨','主任医师','哈尔滨医科大学附属第四医院','急诊科','冠心病,心衰,急性胃肠炎高血压,心绞痛,心肌梗死,心律失常,房颤,早搏,支架治疗。','周四、周六','从2003到至今哈尔滨医科大学附属第四医院',13,2,'17@qq.com'),(19,'18','赵长久','副主任医师','哈尔滨医科大学附属第四医院','临床','。','周四','。',14,2,'18@qq.com'),(20,'19','许春海','主任医师','哈尔滨医科大学附属第二医院','内科','	病毒性肝炎，重型肝炎，出血热，感染性疾病。','周四、周五','许春海，男，教授，擅长病毒性肝炎，重型肝炎，出血热，感染性疾病。',2,3,'19@qq.com'),(21,'20','邱晓红','主任医师','哈尔滨医科大学附属第二医院','妇产科','	子宫肌瘤、子宫内膜异位症、妇科恶性肿瘤、压力性尿失禁、盆腔器官脱垂等疾病的诊治；尤其擅长应用腹腔镜、宫腔镜、盆底重建术在妇科疾病的微创治疗。','周二','邱晓红 女 主任医师 教授 医学博士 硕士研究生导师 发表论文近30篇，承担省、市攻关课题多项，擅长子宫肌瘤、子宫内膜异位症、妇科恶性肿瘤、压力性尿失禁、盆腔器官脱垂等疾病的诊治；尤其擅长应用腹腔镜、宫腔镜、盆底重建术在妇科疾病的微创治疗。',5,3,'20@qq.com'),(22,'21','任丽红','副主任医师','哈尔滨医科大学附属第二医院','儿科','。','周四、周六','收录中...',6,3,'21@qq.com'),(23,'22','刘建宇','副主任医师','哈尔滨医科大学附属第二医院','骨科','主要从事骨与关节损伤的外科治疗，专业特长是复杂骨和关节损伤的内外固定技术，关节镜修复软骨，骨不愈合的治疗。','周三','收录中...',7,3,'22@qq.com'),(24,'23','王秀梅','副主任医师','哈尔滨医科大学附属第二医院','口腔科','	擅长牙齿美容性修复，地方性口腔疾病（氟斑牙）的诊断与治疗，疑难牙体牙髓病、根尖周。','周三、周四、周五','收录中',8,3,'23@qq.com'),(25,'24','张水华','主任医师','哈尔滨医科大学附属第二医院','眼科','小儿眼科及斜视的诊疗，白内障超声乳化手术。','周一','无',9,3,'24@qq.com'),(26,'25','任潞雪','主任医师','哈尔滨医科大学附属第二医院','中医科','。','周三','收录中...',10,3,'25@qq.com'),(27,'26','栗玉珍','副主任医师','哈尔滨医科大学附属第二医院','皮肤病科','.','周三、周四、周五','收录中...',11,3,'26@qq.com'),(28,'27','王鑫','主任医师','哈尔滨医科大学附属第二医院','耳鼻喉科','	鼻内窥镜及相关外科微创手术治疗，咽喉部疾病及肿瘤的手术治疗，耳显微外科治疗。','周三','王鑫，女，医学博士，主任医师，教授，硕士生导师，1998年毕业于哈尔滨医科大学临床医学专业，2001年哈尔滨医科大学耳鼻咽喉科学硕士研究生毕业，并留校于哈尔滨医科大学附属第二医院耳鼻咽喉科工作。2005年吉林大学耳鼻咽喉科学博士研究生毕业，师从我国著名鼻科学专家董震教授，进行了慢性鼻窦炎，鼻息肉基础及临床治疗的深入研究。擅长鼻内窥镜微创治疗鼻部疾病及相关外科治疗，包括常见疾病鼻中隔偏曲，慢性鼻窦炎，鼻息肉，真菌性鼻窦炎，鼻窦囊肿，鼻腔及鼻窦良性及恶性肿瘤，鼻腔及鼻窦异物，先天性后鼻孔闭锁等。同时擅长咽喉部疾病及肿瘤的手术治疗及微创治疗，耳显微外科的治疗。',12,3,'27@qq.com'),(29,'28','孙玉雪','主任医师','哈尔滨医科大学附属第二医院','急诊科','对内急诊各种危重症、各种中毒有丰富的临床经验。在鉴别诊断及急危重症的抢救方面积累了丰富的知识。','周一','收录中...',13,3,'28@qq.com'),(30,'30','金刚','主任医师','哈尔滨医科大学附属第二医院','临床','甲状腺疾病的诊断与治疗（尤其是甲亢碘-131治疗等）。','周四、周五','金刚，男，主任医师，教授，科主任，擅长影像核医学和治疗核医学（尤其是甲亢碘-131治疗等）。现任中华核医学会治疗学组委员、中华医学会黑龙江省核医学会侯任主任委员、中国核学会黑龙江省分会理事、中国医学影像技术研究会核医学分会第三届全国委员、美国核医学会会员、黑龙江省医院协会医学影像学会常务委员、中国医学影像学会黑龙江省分会核医学委员会委员兼秘书、多项国家级和省部级科研项目或科研成果评审专家、中华医学会核医学分会核医学科普专家、省及市医疗事故技术鉴定专家。1985年毕业于哈尔滨医科大学医疗系本科，获学士学位；1994年北京医科大学（现北京大学）核医学专业研究生毕业，获硕士学位；1999年-2000年在美国匹兹堡大学留学研修核医学。现任哈医大二院核医学科主任、主任医师、教授、研究生导师。承担或主持国家级及省部级科研课题或教育科研课题等20余项；发表国际、国家级及省级论文80余篇；主编著作5部，参编8部，主编卫生部医学视听教材2部，参编高校教材5部；获省部级科技成果奖、省教育科研成果奖及省医疗新技术奖等30余项。现从事核医学医疗、教学与科研工作27年，具有扎实的理论基础和较丰富的临床实践经验，',14,3,'30@qq.com'),(31,'31','尹新华','主任医师','哈尔滨医科大学附属第一医院','内科','心血管常见病、多发病及疑难危重症的诊断和抢救治疗，尤其是对冠心病、高血压病、心肌病、心力衰竭、各种心律失常及先天性心脏病的诊治具有丰富经验','周三','尹新华，主任医师，教授，1987年毕业于哈尔滨医科大学医疗系，1990年获医学硕士学位后留校工作，1998年获内科心血管专业博士学位，1999年赴日本国立循环器病中心及大阪大学进行国际共同合作研究，2002年博士后流动站出站。擅长心血管常见病、多发病及疑难危重症的诊断和抢救治疗，尤其是对冠心病、高血压病、心肌病、心力衰竭、各种心律失常及先天性心脏病的诊治具有丰富经验。掌握经胸超声心动图和食管超声心动图的诊疗技术，特别是先天性心脏病超声诊断技术。医疗技术精湛、责任心极强、医德高尚、为人师表，深受患者的尊敬和爱戴，多年担任医疗组组长，为医院零投诉先进个人。',2,4,'31@qq.com'),(32,'32','史怀璋','主治医生','哈尔滨医科大学附属第一医院','外科','主要从事脑血管病外科治疗.包括脑动脉瘤栓塞术,脑动脉瘤显微外科夹闭术,脑动脉狭窄血管内支架成形术,脑血管搭桥术,脑血管畸形栓塞术及切除术等脑血管病的介入及手术治疗。','周一','史怀璋，男，哈医大一院神经外科三病房主任，主任医师、教授。博士研究生导师。1993年毕业于哈尔滨医科大学获学士学位；1999年获哈尔滨医科大学神经外科专业硕士学位；2002年获哈尔滨医科大学神经外科博士学位。2005年于北京宣武医院研修，师从凌锋教授。2006年于美国IOWA大学神经介入中心做访问学者，师从Dr.Chalopka。目前为中华医学会神经介入专业委员会委员，中国医师协会神经介入专业委员会委员，黑龙江省医师协会神经外科分会秘书。黑龙江省医师协会神经介入学组组长。哈尔滨医学会神经外科专业委员会副主任委员。《中国脑血管病杂志》编委',3,4,'32@qq.com'),(33,'33','车建华','副主任医师','哈尔滨医科大学附属第一医院','妇产科','熟练掌握妇产科各种疾病的诊断和治疗，尤其擅长子宫颈癌及卵巢癌的规范化治疗，以及妇科粘连性手术。多次到国内著名医院研究学习宫、腹腔镜技术，熟练掌握宫、腹腔镜手术的。','周二','收录中...',5,4,'33@qq.com'),(34,'34','于丽君','副主任医师','哈尔滨医科大学附属第一医院','儿科','.','周二、周四、周五','收录中...',6,4,'34@qq.com'),(35,'35','张震宇','副主任医师','哈尔滨医科大学附属第一医院','骨科','各种骨折、创伤、复杂性手外伤、周围神经损伤及应用显微外科技术进行四肢功能修复重建和治疗股骨头坏死、骨缺损及脊柱脊髓疾病，如腰间盘突出，颈椎病，脊柱肿瘤.特别擅长为肾衰患者做透析用的人工动静脉瘘。','周四','张震宇，男，医学博士，硕士生导师。1990年毕业于黑龙江中医学院获学士学位；1996年获哈医大外科专业硕士学位；2004年攻读哈医大外科专业博士学位。任哈医大骨科副教授，2000年评为硕士研究生导师。现任骨外科一病房副教授。曾发表专业学术论文40余篇，撰写、翻译、审校专业论著3本。获省教育厅科技进步奖2项，省卫生厅科技进步一等奖1项。中华医学会黑龙江手显微外科学会副主任委员，中华医学会黑龙江省骨关节镜学会秘书。中华手外科学会全国青年委员。善于处理各种骨折、创伤、复杂性手外伤、周围神经损伤及应用显微外科技术进行四肢功能修复重建和治疗股骨头坏死、骨缺损及脊柱脊髓疾病，主要研究方向为显微外科、微创技术、组织工程等技术在骨科各领域的应用。',7,4,'35@qq.com'),(36,'36','牟明奎','副主任医师','哈尔滨医科大学附属第一医院','口腔科','牙列拥挤、前牙反合、上颌前突、双颌前突的正畸治疗及成人正畸和正颌外科。','周四、周六','牟明奎，男，副主任医师，副教授，口腔医学博士，1993年毕业于哈尔滨医科大学，主要从事口腔正畸工作。擅长牙列拥挤、前牙反合、上颌前突、双颌前突的正畸治疗及成人正畸和正颌外科。',8,4,'36@qq.com'),(37,'37','李志坚','主任医师','哈尔滨医科大学附属第一医院','眼科','白内障、青光眼、角膜病、眼外伤的手术治疗。','周一、周二','李志坚 主任医师，教授，研究员 医学博士，博士后，硕士生导师，眼科三病房副主任。\r\n1989年毕业于哈尔滨医科大学。从事眼科临床工作二十余年，技术专长为白内障、青光眼、角膜病和眼外伤的手术治疗。尤其在白内障的诊断和治疗方面有独到的见解。自1993年起主要从事白内障专业的临床和基础研究工作，熟练掌握现代白内障囊外摘除、超声乳化白内障吸除及青光眼白内障联合手术，正确处理各种复杂性白内障手术及其并发症，至2013年6月共完成白内障手术3万余例，积累了丰富的临床经验。近年多次下乡参加中国残疾人联合会等相关部门组织的白内障手术扶贫工作，积极参与有关部门有关白内障知识的普及宣教工作。国家公派在美国耶鲁大学做访问学者1年，同时被聘为客座教授,在美期间并参加美国的ASCRS和ASOA年会。',9,4,'37@qq.com'),(38,'38','王振宇','副主任医师','哈尔滨医科大学附属第一医院','中医科','。','周二','收录中...',10,4,'38@qq.com'),(39,'39','尤德渊','副主任医师','哈尔滨医科大学附属第一医院','皮肤病科','大学毕业十余年来，一直从事皮肤病的诊疗工作，对于过敏性疾病、银屑病、老年性疾病及皮肤肿瘤的诊断及治疗有较深的造诣。 ','周一、周五','收录中...',11,4,'39@qq.com'),(40,'40','安云崧','主任医师','哈尔滨医科大学附属第一医院','耳鼻喉科','过敏性鼻炎,鼻息肉,鼻窦炎,慢性鼻炎,突发性耳聋。','周二','从2013到至今哈尔滨医科大学附属第一医院',12,4,'40@qq.com'),(41,'41','张晓川','副主任医师','哈尔滨医科大学附属第一医院','急诊科','阑尾炎。','周五','从2015到至今哈尔滨医科大学附属第一医院',13,4,'41@qq.com'),(42,'42','芮东红','副主任医师','哈尔滨医科大学附属第一医院','临床','对甲状腺疾病的诊断、治疗，核医学影像诊断等，特别是对131I治疗甲亢积累了丰富的临床经验，已施治了大量患者，获得满意疗效。','周日','收录中..',14,4,'42@qq.com'),(43,'43','金政锡','主治医生','黑龙江省医院','外科','普外一。','周一','普外一。',3,5,'43@qq.com'),(44,'44','赵宏伟','主任医师','黑龙江省医院','内科','	擅长各类型肝炎 (乙丙肝,酒精肝,药物肝等) 、肝硬化(腹水，肝性脑病，上消化道出血等)、疑难肝病、 出血热、艾滋病，发热待查等 疾病的诊治。尤其擅长重型肝炎、肝衰竭、自免疫性肝病、原因不明的转氨酶','周一','现任中华医学会流行病学会委员。省流行病学会主任委员省传染病学会副主任委员。省医师协会传染病分会副主任委员。省委保健委员会专家委员。中华重型肝病血液净化学组委员。省艾滋病协会委员。擅长各类型肝炎、肝硬化、疑难肝病、流行性出血热、艾滋病，发热待查等感染性疾病的诊治。尤其擅长重型肝炎、急慢性肝衰竭、免疫性肝病、原因不明的转氨酶升高等疑难杂症及各种发热性疾病。带领科室人员开展多项新技术：利用肝穿检查确诊数百例疑难病例；利用腹水回输治疗难治性腹水；利用自体骨髓干细胞移植治疗终末期肝硬化，重症肝炎等；利用人工肝技术抢救重症肝炎及肝衰竭。',2,5,'44@qq.com'),(45,'45','王立','主任医师','黑龙江省医院','妇产科','擅长妇科常见及疑难疾病的诊断及治疗。针对妇科恶性肿瘤达到国内先进水平，擅长腹腔镜及宫腔镜等微创手术。','周二','1991年毕业于哈尔滨医科大学，于2007年~2008年赴日本砺波综合病院研修一年，主修妇科肿瘤及微创手术技术。担任全国妇科内镜微创技术推广专家委员、全国性病学会专家委员、中华医学会黑龙江省医学会妇科分会委员、省女医师协会常务理事、医疗事故鉴定专家组成,省妇女保健协会副主任委员；黑龙江省专家保健组成员. 于1991年7月分配到黑龙江省医院妇产科工作至今。曾于2007年~2008年公派赴日本砺波综合病院研修一年，主攻妇科肿瘤及微创手术.现任黑龙江省医院妇科室主任，曾获省级科研成果二等奖两项、新技术成果奖9项。担任中国性学会性医学专业委会委员，全国腔镜手术推广组专家成员，黑龙江省医学会妇产专业委员会委员，省老年医学会妇产科分会主任委员，黑龙江省女医师协会常务理事，哈尔滨市医师协会副主任委员，黑龙江省专家保健组成员，哈尔滨市医学会医疗事故鉴定专家库成员。从事妇产科20余年，擅长妇科常见及疑难疾病的诊断及治疗。针对妇科恶性肿瘤达到国内先进水平，擅长腹腔镜及宫腔镜等微创手术。',5,5,'45@qq.com'),(47,'46','赵雪妮','副主任医师','黑龙江省医院','儿科','	除擅长儿科危急重症及新